import React, { useState } from 'react';
import './Login.css';
import Navbar from './navigation.jsx';
import Sidebar from './navigation/sidebar.jsx';
import { generalPost } from './reusable/functions.jsx';

const AddTiming = () => {
  const [duration, setDuration] = useState();
  const [timings, setTimings] = useState({});
  const [errorMessage, setErrorMessage] = useState('');
  const [name, setName] = useState('')
  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

  const handleCheckboxChange = (day) => {
    setTimings(prevTimings => {
      if (prevTimings[day]) {
        const { [day]: _, ...rest } = prevTimings;
        return rest;
      } else {
        return { ...prevTimings, [day]: { startTime: '', endTime: '' } };
      }
    });
  }

  const handleTimeChange = (day, timeType, value) => {
    setTimings(prevTimings => {
      const updatedDay = { ...prevTimings[day], [timeType]: value };
      return { ...prevTimings, [day]: updatedDay };
    });
    console.log(timings);
  }

  const handleNameChange = (e)=>{
    setName(e.target.value);
  } 
  const handleDurationChange = (e)=>{
    setDuration(e.target.value);
  } 

  const submit = async (e) => {
    e.preventDefault();

    for (let day in timings) {
      const { startTime, endTime } = timings[day];
      if (endTime < startTime) {
        setErrorMessage(`End Time for ${day} cannot be before Start Time`);
        return;
      }
    }
    const body = {
      name:name,
      duration :duration,
      timings: timings

    };

    try {
      const data = await generalPost('viewsets/timesets', body);
      if (!data) {
        setErrorMessage(`Timeset already exists`);
      } else {
        alert(`Timeset added successfully!`);
      }
    } catch (error) {
      console.log(error);
    }

  }

  return (
    <div>
      <Navbar title="Home" isLoggedIn={localStorage.getItem('isLogged')} fname={localStorage.getItem('fname')} />

      <div className='mt-9 rounded-sm border border-stroke bg-white py-6 px-10 shadow-default dark:border-strokedark dark:bg-boxdark'>
        <form onSubmit={submit}>
          <h1 className='text-title-md2 font-semibold text-primary dark:text-white centerholder'>Add Timeset</h1>
          <br />

          <div className="centerholder input-box mb-2">
            <input 
              id="name"
              type="text" 
              name="name"
              placeholder="Timeset Name" 
              value={name} 
              onChange={handleNameChange} 
              required             />
          </div>
          <div className="centerholder input-box mb-2">
            <input 
              id="duration"
              type="number" 
              name="duration"
              placeholder="Lesson Duration (minutes)" 
              value={duration} 
              onChange={handleDurationChange} 
              required             />
          </div>

          {daysOfWeek.map(day => (
            <div key={day}>
              <span className='inline'>
                <input
                  id={day}
                  type="checkbox"
                  value={day}
                  className='w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600'
                  onChange={() => handleCheckboxChange(day)}
                />
                <label htmlFor={day}>{day}: </label>

                <label htmlFor={`${day}-startTime`}>Start Time:</label>
                <input
                  type="time"
                  id={`${day}-startTime`}
                  placeholder="Start Time"
                  value={timings[day]?.startTime || ''}
                  onChange={(e) => handleTimeChange(day, 'startTime', e.target.value)}
                  disabled={!timings[day]}
                  required={!!timings[day]}
                />

                <label htmlFor={`${day}-endTime`}>End Time:</label>
                <input
                  type="time"
                  id={`${day}-endTime`}
                  placeholder="End Time"
                  value={timings[day]?.endTime || ''}
                  onChange={(e) => handleTimeChange(day, 'endTime', e.target.value)}
                  disabled={!timings[day]}
                  required={!!timings[day]}
                />
              </span><hr className='mb-0.5'/>
            </div>
          ))}

          <div className="centerholder">
            <button type="submit" className='justify-center inline-flex items-center justify-center bg-primary py-4 px-10 text-center font-medium text-white hover:bg-opacity-90 lg:px-8 xl:px-10" type="submit">Add</button>'>Add</button>
          </div>

          <div className="warning centerholder">
            {errorMessage && <p>{errorMessage}</p>}
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddTiming;
