import React, { useEffect, useState } from 'react';
import { Pie,Bar,Doughnut } from 'react-chartjs-2';
import { fetchVisitData } from '../services/visitService';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';



export const VisitsPerInstitutionChart = () => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchVisitData('per_institution');
      if (data) {
        setChartData({
          labels: Object.keys(data),
          datasets: [{
            data: Object.values(data),
            backgroundColor: [
              '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'
            ],
          }]
        });
      }
    };
    fetchData();
  }, []);

  if (!chartData) return <div>Loading...</div>;

  return (
    <div>
      <h2>Visits Per Institution</h2>
      <Pie data={chartData} />
    </div>
  );
};

export const VisitsPerTableChart = () => {
    const [chartData, setChartData] = useState(null);
  
    useEffect(() => {
      const fetchData = async () => {
        const data = await fetchVisitData('per_table');
        if (data) {
          setChartData({
            labels: Object.keys(data),
            datasets: [{
              label: 'Visits per Table',
              data: Object.values(data),
              backgroundColor: '#36A2EB',
            }]
          });
        }
      };
      fetchData();
    }, []);
  
    if (!chartData) return <div>Loading...</div>;
  
    return (
      <div>
        <h2>Visits Per Table</h2>
        <Bar data={chartData} />
      </div>
    );
};

export const VisitsPerActionChart = () => {
    const [chartData, setChartData] = useState(null);
  
    useEffect(() => {
      const fetchData = async () => {
        const data = await fetchVisitData('per_action');
        if (data) {
          setChartData({
            labels: Object.keys(data),
            datasets: [{
              data: Object.values(data),
              backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
            }]
          });
        }
      };
      fetchData();
    }, []);
  
    if (!chartData) return <div>Loading...</div>;
  
    return (
      <div>
        <h2>Visits Per Action</h2>
        <Doughnut data={chartData} />
      </div>
    );
};

export const VisitsPerDaypartChart = () => {
    const [chartData, setChartData] = useState(null);
  
    useEffect(() => {
      const fetchData = async () => {
        const data = await fetchVisitData('per_daypart');
        if (data) {
          setChartData({
            labels: Object.keys(data),
            datasets: [{
              data: Object.values(data),
              backgroundColor: [
                '#FF6384', // morning
                '#36A2EB', // afternoon
                '#FFCE56', // evening
                '#4BC0C0', // night
              ],
              hoverBackgroundColor: [
                '#FF6384',
                '#36A2EB',
                '#FFCE56',
                '#4BC0C0',
              ]
            }]
          });
        }
      };
      fetchData();
    }, []);
  
    if (!chartData) return <div>Loading...</div>;
  
    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top',
        },
        title: {
          display: true,
          text: 'Visits Per Daypart',
        },
      },
    };
  
    return (
      <div style={{ width: '400px', height: '400px' }}>
        <Pie data={chartData} options={options} />
      </div>
    );
};
