import React, { useEffect, useState } from 'react';
import { searchFunction } from './reusable/functions';
import { Link } from 'react-router-dom';
import Navbar from './navigation';

const InstitutionProfile = () => {
  const [institution, setInstitution] = useState(null);

  const fetchInstitutionDetails = async () => {
    try {
      const data = await searchFunction('viewsets/institutions', { search: localStorage.getItem('institution') });
      if (data.length > 0) {
        setInstitution(data[0]);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchInstitutionDetails();
  }, []);

  if (!institution) {
    return <div>Loading...</div>;
  }

  return (
    <><Navbar title="Home"></Navbar>
      <div className="overflow-hidden rounded-sm border border-stroke bg-white shadow-default dark:border-strokedark dark:bg-boxdark">
        <div className="relative z-20 h-35 md:h-65">
          <img
            src='https://live.staticflickr.com/4574/24791500728_8fd1eeeebc_c.jpg'
            alt="profile cover"
            className="w-full rounded-tl-sm rounded-tr-sm object-cover object-center"
          />
        </div>

        <div className="px-4 pb-6 text-center lg:pb-8 xl:pb-11.5">
          <h3 className="text-xl font-semibold text-black dark:text-white">
            {institution.name}
          </h3>
          <hr />
          <br />
          <div className="mt-4">

            <p className="text-sm text-gray-600 dark:text-gray-400">
              Email: {institution.email}
            </p>
            <br />
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Phone: {institution.phone}
            </p>
          </div>
          <div className="mt-6">
            <p className="text-sm text-blue-500 hover:underline">
              Edit Profile
            </p>
            <Link to='/notifications' className="text-sm text-blue-500 hover:underline">Notifcations(10)</Link>
            <br />
            <Link to='/notifications' className="text-sm text-blue-500 hover:underline">Schedules</Link>
          </div>
        </div>
      </div>
    </>
  );
};

export default InstitutionProfile;
