import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../navigation';
import { generalPatch, generalDelete, searchFunction,makeNotification } from '../reusable/functions';

const CourseTable = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [courses, setCourses] = useState([]);
    const [editCourseId, setEditCourseId] = useState(null);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [deleteCourseId,setDeleteCourseId] = useState(null);
    const [deleteCourseName,setDeleteCourseName] = useState(null);

    const [editFormData, setEditFormData] = useState({
        course_number: '',
        course_name: '',
        max_numb_students: ''
    });
    // console.log(courses)
    const fetchData = async () => {
        try {
            const data = await searchFunction('viewsets/courses', { search: searchQuery });
            setCourses(data);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        fetchData();
    }, [searchQuery]);

    const handleEditClick = (course) => {
        setEditCourseId(course.id);
        setEditFormData({
            course_number: course.course_number,
            course_name: course.course_name,
            max_numb_students: course.max_numb_students
        });
    };

    const handleEditFormChange = (e) => {
        const { name, value } = e.target;
        setEditFormData({
            ...editFormData,
            [name]: value
        });
    };

    const handleCancelClick = () => {
        setEditCourseId(null);
        setEditFormData({
            course_number: '',
            course_name: '',
            max_numb_students: ''
        });
    };

    const handleSaveClick = async () => {
        try {
            await generalPatch('viewsets/courses/', editCourseId, editFormData);
            await makeNotification(`Course ${editFormData.course_name} has been updated by Scheduler`);
            setEditCourseId(null);
            fetchData(); // Refresh the data
        } catch (error) {
            console.log(error);
        }
    };

    const handleDeleteClick = async (courseId,courseName) => {
        // try {
        //     await generalDelete('viewsets/courses/', courseId);
        //     await makeNotification(`Course ${editFormData.course_name} has been deleted by Scheduler`);
        //     fetchData(); // Refresh the data
        // } catch (error) {
        //     console.log(error);
        // }
        setDeleteRoomId(courseId)
        setDeleteRoomName(courseName)
        setShowDeleteModal(true)
        fetchData();
    };

    return (
        <div>
            <Navbar title="Home" />
            <div className="centerholder">
                <DeleteModal show={showDeleteModal} id={deleteRoomId} name={deleteRoomName} endpoint={'viewsets/rooms/'} onClose={() => setShowDeleteModal(false)} itemKind={'Room'}></DeleteModal>
            </div>
            <div className="rounded-sm border border-stroke bg-white px-5 pt-6 pb-2.5 shadow-default dark:border-strokedark dark:bg-boxdark sm:px-7.5 xl:pb-1">
                <h4 className="mb-6 text-xl font-semibold text-black dark:text-white">
                    Courses
                </h4>

                <div className="flex flex-col">
                    <div className="grid grid-cols-3 rounded-sm bg-gray-2 dark:bg-meta-4 sm:grid-cols-5">
                        <div className="p-2.5 xl:p-5">
                            <h5 className="text-sm font-medium uppercase xsm:text-base">
                                Course Number
                            </h5>
                        </div>
                        <div className="p-2.5 text-center xl:p-5">
                            <h5 className="text-sm font-medium uppercase xsm:text-base">
                                Course Name
                            </h5>
                        </div>
                        <div className="p-2.5 text-center xl:p-5">
                            <h5 className="text-sm font-medium uppercase xsm:text-base">
                                Max Students
                            </h5>
                        </div>
                        <div className="p-2.5 text-center xl:p-5">
                            <h5 className="text-sm font-medium uppercase xsm:text-base">
                                Update
                            </h5>
                        </div>
                        <div className="p-2.5 text-center xl:p-5">
                            <h5 className="text-sm font-medium uppercase xsm:text-base">
                                Delete
                            </h5>
                        </div>
                    </div>

                    {courses.map((course, key) => (
                        
                        <div
                            className={`grid grid-cols-3 sm:grid-cols-5 ${
                                key === courses.length - 1 ? '' : 'border-b border-stroke dark:border-strokedark'
                            }`}
                            key={key}
                        >
                            {editCourseId === course.id ? (
                                <>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <input
                                            type="text"
                                            name="course_number"
                                            value={editFormData.course_number}
                                            onChange={handleEditFormChange}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <input
                                            type="text"
                                            name="course_name"
                                            value={editFormData.course_name}
                                            onChange={handleEditFormChange}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <input
                                            type="text"
                                            name="max_numb_students"
                                            value={editFormData.max_numb_students}
                                            onChange={handleEditFormChange}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <button onClick={handleSaveClick} className="btn btn-primary">Save</button>
                                        <button onClick={handleCancelClick} className="btn btn-secondary ml-2">Cancel</button>
                                    </div>
                                </>
                            ) : (
                                <>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <p className="text-black dark:text-white">{course['course_number']}</p>
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <p className="text-black dark:text-white">{course['course_name']}</p>
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <p className="text-black dark:text-white">{course['max_numb_students']}</p>
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <button onClick={() => handleEditClick(course)} className="btn btn-warning">Edit</button>
                                    </div>
                                    <div className="flex items-center justify-center p-2.5 xl:p-5">
                                        <button onClick={() => handleDeleteClick(course.id)} className="btn btn-danger">Delete</button>
                                    </div>
                                </>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default CourseTable;
