import { useState } from "react";
import { generalDelete, makeNotification } from "./functions";

export const DeleteConfirmationModal = ({ show, onClose, onConfirm, name }) => {
    if (!show) return null;

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-lg">
                <h2 className="text-xl mb-4">Confirm Deletion</h2>
                <p>Are you sure you want to delete the "{name}"?</p>
                <div className="mt-4 flex justify-end">
                    <button onClick={onClose} className="btn btn-secondary mr-2">Cancel</button>
                    <button onClick={onConfirm} className="btn btn-danger">Delete</button>
                </div>
            </div>
        </div>
    );
};
export const DeleteModal = ({show,name,endpoint,id }) => {
    const [internalShow, setInternalShow] = useState(show);
    if (!internalShow) return null;

    const [status,setStatus] = useState('')
    const onConfirm = async(id)=>{
        try {
            setStatus("Deleting...");
            await generalDelete( endpoint, id);
            await makeNotification(`Room ${name} has been removed by Scheduler`);
            setInternalShow(false);
        } catch (error) {
            console.log(error)
        }
    }
    const onClose = ()=>{
        setInternalShow(false);
    }

    return (
        <dialog className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-lg">
                <h2 className="text-xl mb-4">Confirm Deletion</h2>
                <p>Are you sure you want to delete the "{name}"?</p>
                <div className="mt-4 flex justify-end">
                    <button onClick={onClose} className="btn btn-secondary mr-2">Cancel</button>
                    <button onClick={()=>{onConfirm(id)}} className="btn btn-danger">Delete</button>
                </div>
                {status}
            </div>
        </dialog>
    );
};


