import React, { useState, useEffect } from 'react';
import { searchFunction, generalPost } from '../reusable/functions';

const CommentComponent = ({ scheduleId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');

    useEffect(() => {
        fetchComments();
    }, [scheduleId]);

    const fetchComments = async () => {
        try {
            const data = await searchFunction('viewsets/comments', { schedule: scheduleId });
            setComments(data);
        } catch (error) {
            console.error('Error fetching comments:', error);
        }
    };

    const handleSubmitComment = async (e) => {
        e.preventDefault();
        try {
            await generalPost('viewsets/comments', {
                content: newComment,
                schedule: scheduleId
            });
            setNewComment('');
            fetchComments();
        } catch (error) {
            console.error('Error posting comment:', error);
        }
    };

    return (
        <div className="comments-container">
            <h3>Comments</h3>
            <div className="comments-list">
                {comments.map((comment) => (
                    <div key={comment.id} className="comment">
                        <p>{comment.content}</p>
                        <small>
                            By {comment.commenter.fname} {comment.commenter.lname} 
                            on {new Date(comment.time).toLocaleString()}
                        </small>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSubmitComment} className="new-comment-form">
                <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Write a comment..."
                    required
                />
                <button type="submit">Post Comment</button>
            </form>
        </div>
    );
};

export default CommentComponent;
