import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../Login.css';
import axios from 'axios';
import { generalPatch } from './functions';

// Modal Component for Registering an Institution
const RegisterInstitutionModal = ({ isOpen, onClose, onRegister }) => {
    const [institutionData, setInstitutionData] = useState({
        name: '',
        address: '',
        email: '',
        phone: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setInstitutionData({ ...institutionData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onRegister(institutionData);
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h2>Register Institution</h2>
                <form onSubmit={handleSubmit}>
                    <input type="text" name="name" placeholder="Institution Name" onChange={handleChange} required />
                    <input type="text" name="address" placeholder="Address" onChange={handleChange} required />
                    <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
                    <input type="text" name="phone" placeholder="Phone" onChange={handleChange} required />
                    <button type="submit">Register</button>
                </form>
            </div>
        </div>
    );
};

const HomeTiles = ({ is_institution_approved, is_application_accepted, role, institution }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const fetchData = async () => {
        try {
            const response = await axios.get('/api/viewsets/institution', { params: { search: searchQuery } });
            setSearchResults(response.data);
        } catch (error) {
            console.log(error);
        }
    };

    const handleInstitutionSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };
    const handleInstructorOptionClick = () => {
        try {
            generalPatch('viewsets/institutionmembers/', localStorage.getItem('user_id'), { role: 'instructor' })
        } catch (error) {
            alert(error)
        }
    }
    const handleSchedulerOptionClick = () => {
        try {
            generalPatch('viewsets/institutionmembers/', localStorage.getItem('user_id'), { role: 'scheduler' })
        } catch (error) {
            alert(error)
        }
    }//confirm if right


    const handleInstitutionClick = async (institutionId) => {
        try {
            await axios.patch(`/api/viewsets/institutionmembers/${localStorage.getItem('user_id')}`, { institution: institutionId });
            // Update user state to reflect the new institution
        } catch (error) {
            console.log(error);
        }
    };

    const handleRegisterInstitution = async (institutionData) => {
        try {
            await axios.post('/api/viewsets/institution', institutionData);
            setIsModalOpen(false);
            // Fetch data again to include the new institution in search results
            fetchData();
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        if (searchQuery) {
            fetchData();
        }
    }, [searchQuery]);

    const renderActions = () => {
        console.log(role)
        if (role==='unassigned') {
            return (
                <div>
                    Do You Want To Use Timetabulous as an
                    <div onClick={() => {handleInstructorOptionClick()}}>Instructor</div>
                    <div onClick={() => {handleSchedulerOptionClick()}}>Scheduler</div>
                </div>
            );
        } else if (role === 'scheduler' && !is_institution_approved) {
            return (
                <div className="">
                    Your Institution is Awaiting Approval
                </div>
            );
        } else if (role === 'scheduler' && !institution) {
            return (
                <div className="">
                    <div onClick={() => setIsModalOpen(true)}>Register Institution</div>
                    <div onClick={() => {/* logic to open search modal */}}>Search for Institution</div>
                    <div className="search">
                        <input onChange={handleInstitutionSearchChange} name="searchQuery" placeholder="Search Institution" />
                        <div className="results">
                            {searchResults.map((result) => (
                                <div key={result.id} onClick={() => handleInstitutionClick(result.id)}>{result.name}</div>
                            ))}
                        </div>
                    </div>
                </div>
            );
        } else if (role === 'instructor' && !institution) {
            return (
                <div className="">
                    <div className="search">
                        <input onChange={handleInstitutionSearchChange} name="searchQuery" placeholder="Search Institution" />
                        <div className="results">
                            {searchResults.map((result) => (
                                <div key={result.id} onClick={() => handleInstitutionClick(result.id)}>{result.name}</div>
                            ))}
                        </div>
                    </div>
                </div>
            );
        } else if (role === 'admin') {
            return (
                <div className="">
                    Welcome
                    <div><Link to="/dashboard">View Dashboard</Link></div>
                    <div><Link to="/profile">View Profile</Link></div>
                </div>
            );
        } else if (role === 'scheduler') {
            return (
                <div className="">
                    Welcome
                    <div><Link to="/schedules">View Schedules</Link></div>
                    <div><Link to="/constraints">View Constraints</Link></div>
                    <div><Link to="/messages">View Messages</Link></div>
                </div>
            );
        } else if (role === 'instructor') {
            return (
                <div className="">
                    Welcome
                    <div><Link to="/messages">Most Recent Message (Click To See More Messages...)</Link></div>
                    <div><Link to="/schedules">View Schedules</Link></div>
                </div>
            );
        }
    };

    return (
        <div className="">
            <div className="">Welcome</div>
            {renderActions()}
            <RegisterInstitutionModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onRegister={handleRegisterInstitution} />
        </div>
    );
}

export default HomeTiles;
