import React,{ useEffect, useState } from "react";
import Navbar from "./navigation";
import searchFunction from './reusable/functions';  

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [errorMsg, setErrorMsg] = useState('');
    const fetchNotifications = async () => {
        searchFunction('viewsets/notifications').then((data) => {
            if (!data) {
                setErrorMsg("No notifications yet");
                return;
            }
            setNotifications(data);
        }).catch((error) => {
            console.log(error)
        })
    }
    const markAsRead = async (id) => {
        try {
            searchFunction(`viewsets/notifications/mark_read`).then((data) => {
                fetchNotifications();
            }).catch((error) => {
                console.log(error)
                setErrorMsg("unable to mark as read");
            })
        } catch (error) {
            setErrorMsg(error);
        }
    }

    useEffect(() => {
        fetchNotifications();
    }, []);

    return (
        <div><Navbar title="Home"/>
            <h1 className="text-title-md2 font-semibold text-primary dark:text-white centerholder">Notifications</h1>
            {errorMsg && <p className="text-red-500">{errorMsg}</p>}
            
            {notifications.map((notification) => (
                <div key={notification.id} className="w-5/6 border border-gray-300 p-4 mb-4">
                    <p className="text-black-700">{notification.description}</p>
                    <span className="text-gray-500 text-sm justify-end">notification.time</span>
                </div>
            ))}

        </div>
    );
};

export default Notifications;
