import Navbar from "../navigation";
import React from "react";
import { Graphs, InstitutionCrud, UserCrud, VisitsPerActionChart, VisitsPerDaypartChart, VisitsPerInstitutionChart, VisitsPerTableChart } from "./dashboardcomponents";
// import Navbar from "../navigation";

const Dash = () => {
    const [section, setSection] = React.useState('Graphs');
    
    const handleSectionClick = (next_section) => {
        setSection(next_section);
    };

    return(
        <><Navbar title="Home"/>
            <h1 className="text-primary text-center text-grey">Dashboard</h1>
            <div className="centerholder">
                <button className="button" onClick={() => handleSectionClick('Institutions')}>Institutions</button>
                <button className="button" onClick={() => handleSectionClick('Users')}>Users</button>
                <button className="button" onClick={() => handleSectionClick('Graphs')}>Graphs</button>
            </div>
            {section === 'Institutions' && <InstitutionCrud />}
            {section === 'Users' && <UserCrud />}
            {section === 'Graphs' && <Graphs/>}
            
        </>
    );

};

export default Dash;
