import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { searchFunction } from '../reusable/functions';

const Timetable = () => {
  const [schedules, setSchedules] = useState({});
  const [days, setDays] = useState([]);
  const [times, setTimes] = useState([]);
  const {timetableId} = useParams();

  useEffect(() => {
    const fetchTimetable = async () => {
      try {
        const response = await searchFunction(`/viewsets/lessondetails/table/?search=${timetableId}`);
        console.log(response)
        setSchedules(response);
        
        const allDays = new Set();
        const allTimes = new Set();
        Object.values(response.data).forEach(stream => {
          Object.keys(stream).forEach(day => {
            allDays.add(day);
            Object.keys(stream[day]).forEach(time => allTimes.add(time));
          });
        });
        setDays(Array.from(allDays).sort());
        setTimes(Array.from(allTimes).sort());
      } catch (error) {
        console.error('Error fetching timetable:', error);
      }
    };

    fetchTimetable();
  }, [timetableId]);

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2 border">Day / Time</th>
            {times.map(time => (
              <th key={time} className="px-4 py-2 border">{time}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {days.map(day => (
            <tr key={day}>
              <th className="px-4 py-2 border bg-gray-100">{day}</th>
              {times.map(time => {
                const lesson = Object.values(schedules).find(
                  stream => stream[day] && stream[day][time]
                );
                return (
                  <td key={`${day}-${time}`} className="px-4 py-2 border">
                    {lesson ? (
                      <div>
                        <p className="font-bold">{lesson[day][time].course}</p>
                        <p className="text-sm">{lesson[day][time].instructor}</p>
                        <p className="text-xs text-gray-500">{lesson[day][time].room}</p>
                      </div>
                    ) : null}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Timetable;
